#include "font.h"

uint8_t* font_get_char(const font* currentFont, char character)
{
	uint8_t characterNumber;
	uint16_t characterPosition;

	const uint8_t* characterPointer;

	//Get character number (substract ASCII offset)
	characterNumber = ((uint8_t) character) - currentFont->startingCharacter;
	//Get character starting position in font array
	characterPosition = ((uint16_t) characterNumber) * currentFont->bytesPerCharacter;

	//Check if requested char exists in array
		//if (characterNumber >= 0 || characterNumber < currentFont->characterNum)
	if (characterNumber < currentFont->characterNum)
	{
		//If character exists, return its position in the array
		characterPointer = &(currentFont->fontArray[characterPosition]);
	}
	else
	{
		//If it doesn't, return '?'
		characterPointer = &(currentFont->fontArray[31*currentFont->bytesPerCharacter]);
	}

	return (uint8_t*) characterPointer;
}

